function SearchComponent() {
    const [results, setResults] = useState([]);
    const [query, setQuery] = useState('');

    useEffect(() => {
        let cancelled = false;

        fetch(`/api/search?q=${query}`)
            .then(r => r.json())
            .then(data => {
                if (!cancelled) {
                    setResults(data);
                }
            });

        return () => { cancelled = true; };
    }, [query]);

    return <div>...</div>;
}

